mapper.select:
select exam_question as examQuestion, exam_choice1 as examChoice1, exam_choice2 as examChoice2, exam_choice3 as examChoice3, exam_choice4 as examChoice4, exam_choice5 as examChoice5, exam_answer as examAnswer, exam_content as examContent from exam where exam_kind = #{examKind}
and subject_code = #{subjectCode}
and exam_num = #{examNum}
